import React, { useState } from 'react';
import './Signuppage.css';
import { auth, db } from '../firebaseconfig';
import { createUserWithEmailAndPassword } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";
import arrowIcon from '../../assets/arrowblack.png';
import googleIcon from '../../assets/google.png';
import githubIcon from '../../assets/github.png';

const SignupPage = () => {
  const [usercreds, setusercreds] = useState({
    email: '',
    password: '',
    confirmpassword: '',
    firstname: '',
    lastname: '',
    dob: '',
    gender: ''
  });

  function handlecreds(e) {
    setusercreds({
      ...usercreds,
      [e.target.name]: e.target.value
    });
  }

  async function handlesignup(e) {
    e.preventDefault();

    // Basic validation
    if (usercreds.password !== usercreds.confirmpassword) {
      alert("Passwords don't match!");
      return;
    }

    try {
      // 1. Create auth user
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        usercreds.email,
        usercreds.password
      );

      // 2. Store additional user data in Firestore
      await setDoc(doc(db, "users", userCredential.user.uid), {
        first_name: usercreds.firstname,
        last_name: usercreds.lastname,
        dob: usercreds.dob,
        gender: usercreds.gender,
        email: usercreds.email,
        createdAt: new Date()
      });

      alert('Account created successfully!');
      // Redirect user or clear form here

    } catch (error) {
      console.error("Error:", error);
      alert(`Signup failed: ${error.message}`);
    }
  }

  return (
    <div className="page-wrapper">
      <div className="container">
        {/* ... other JSX remains the same until gender options ... */}
        <div className="gender-options">
          <p>Enter your gender (Optional):</p>
          <input 
            type="radio" 
            id="male" 
            name="gender" 
            value="Male" 
            onChange={handlecreds}
            checked={usercreds.gender === 'Male'}
          />
          <label htmlFor="male">Male</label>
          <input 
            type="radio" 
            id="female" 
            name="gender" 
            value="Female" 
            onChange={handlecreds}
            checked={usercreds.gender === 'Female'}
          />
          <label htmlFor="female">Female</label>
        </div>
        {/* ... rest of your JSX ... */}
      </div>
    </div>
  );
};

export default SignupPage;
