import React, { useState, useEffect } from 'react';
import './Signuppage.css';
// Import Firebase
// import { initializeApp } from "firebase/app"; 
// import { getAnalytics } from "firebase/analytics";
// Import assets
import arrowIcon from '../../assets/arrowblack.png';
import googleIcon from '../../assets/google.png';
import githubIcon from '../../assets/github.png';

const SignupPage = () => {
  // Firebase initialization
  useEffect(() => {
    const firebaseConfig = {
      apiKey: "AIzaSyDi3wbrtu5mLrkPL-tsnaY-_7Pwysvtoq0",
      authDomain: "course-advisor-20960.firebaseapp.com",
      projectId: "course-advisor-20960",
      storageBucket: "course-advisor-20960.firebasestorage.app",
      messagingSenderId: "500002601601",
      appId: "1:500002601601:web:41b2bb1232b5b6460dab80",
      measurementId: "G-X9TV6LT47Q"
    };

    // const app = initializeApp(firebaseConfig);
    // const analytics = getAnalytics(app);
  }, []);

  return (
    <div className="page-wrapper">
      <div className="container">
        <div className="sidebar">
          <div className="step-indicator">
            <div className="step active"><a href="#1">1. Basic Information</a></div> {/* Added href and fixed closing tag */}
            <div className="step"><a href="#2">2. Skills</a></div> {/* Fixed extra closing tag */}
            <div className="step"><a href="#3">3. Preferences</a></div>
          </div>
        </div>

        <div className="form-container">
          <h2>Create your account</h2>
          <form method="post">
            <input type="email" required placeholder="Email address" />
            <input type="password" required placeholder="Password" />
            <input type="password" required placeholder="Confirm your Password" />
            <input type="text" required placeholder="First Name" />
            <input type="text" required placeholder="Last Name" />
            <input type="date" required placeholder="Date of birth" />

            <div className="gender-options">
              <p>Enter your gender (Optional):</p>
              <input type="radio" id="male" name="gender" value="Male" />
              <label htmlFor="male">Male</label>
              <input type="radio" id="female" name="gender" value="Female" />
              <label htmlFor="female">Female</label>
            </div>

            <button type="button" className="next-button"> {/* Added type="button" */}
              Next <img src={arrowIcon} alt="arrow" />
            </button>

            <div className="separator">
              <span>Or Signup using</span>
            </div>

            <div className="social-buttons">
              <button type="button"><img src={googleIcon} alt="Google" /></button> {/* Wrapped in button for better semantics */}
              <button type="button"><img src={githubIcon} alt="GitHub" /></button> {/* Fixed alt text and wrapped in button */}
            </div>

            <p className="login-link">Already have an account? <a href="/login">Log in</a></p> {/* Added proper href */}
          </form>
        </div>
      </div>
    </div>
  );
};

export default SignupPage;
